		sql := fmt.Sprintf("insert into `%s`.`%s`(a,b,c,o,p,q,r) values(%d,%d,'%d', B'1', , , '2019-4-19 18:03:43')", shift.cfg.FromDatabase, shift.cfg.FromTable, step*4, step, step)
